import "./App.css";
import { useState, useEffect } from "react";
import logo from "./ethereumLogo.svg";
import { Select } from "antd";
import { Routes, Route, Navigate } from "react-router-dom";
import Home from "./components/Home";
import CreateAccount from "./components/CreateAccount";
import RecoverAccount from "./components/RecoverAccount";
import WalletView from "./components/WalletView";
import ImportWallet from "./components/ImportWallet";
import PasswordLogin from "./components/PasswordLogin";
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [wallet, setWallet] = useState(null);
  const [seedPhrase, setSeedPhrase] = useState(null);
  const [selectedChain, setSelectedChain] = useState("0x1");
  const [hasWallet, setHasWallet] = useState(false);

  useEffect(() => {
    // Check if user has a wallet already
    const encryptedData = localStorage.getItem("encryptedWalletData");
    setHasWallet(!!encryptedData);
  }, [wallet]);

  return (
    <div className="App">
      <header>
        <img src={logo} className="headerLogo" alt="logo" />
        <ToastContainer
          position="top-right"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="light"
          style={{
            width: "auto",
            maxWidth: "450px",
            marginTop: "60px"
          }}
        />
        <Select
          onChange={(val) => setSelectedChain(val)}
          value={selectedChain}
          options={[
            {
              label: "Ethereum",
              value: "0x1",
            },
            {
              label: "Polygon",
              value: "0x89",
            },
            {
              label: "Sepolia Testnet",
              value: "0xaa36a7",
            },
          ]}
          className="dropdown"
        ></Select>
      </header>
      {wallet && seedPhrase ? (
        <Routes>
          <Route
            path="/yourwallet"
            element={
              <WalletView
                wallet={wallet}
                setWallet={setWallet}
                seedPhrase={seedPhrase}
                setSeedPhrase={setSeedPhrase}
                selectedChain={selectedChain}
              />
            }
          />
          <Route path="*" element={<Navigate to="/yourwallet" replace />} />
        </Routes>
      ) : (
        <Routes>
          <Route 
            path="/" 
            element={
              hasWallet ? 
                <PasswordLogin 
                  setSeedPhrase={setSeedPhrase} 
                  setWallet={setWallet} 
                /> : 
                <Home />
            } 
          />
          <Route
            path="/login"
            element={
              <ImportWallet
                setSeedPhrase={setSeedPhrase}
                setWallet={setWallet}
              />
            }
          />
          <Route
            path="/recover"
            element={
              <RecoverAccount
                setSeedPhrase={setSeedPhrase}
                setWallet={setWallet}
              />
            }
          />
          <Route
            path="/yourwallet"
            element={
              <CreateAccount
                setSeedPhrase={setSeedPhrase}
                setWallet={setWallet}
              />
            }
          />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      )}
    </div>
  );
}

export default App;
