import React, { useState } from "react";
import { Button, Input } from "antd";
import { useNavigate } from "react-router-dom";
import { toast } from 'react-toastify';

function PasswordLogin({ setSeedPhrase, setWallet }) {
  const navigate = useNavigate();
  const [password, setPassword] = useState("");
  const [showRecovery, setShowRecovery] = useState(false);

  const handleLogin = () => {
    const storedData = localStorage.getItem("walletData");
    if (storedData) {
      const { seedPhrase, wallet, hashedPassword } = JSON.parse(storedData);
      if (btoa(password) === hashedPassword) {
        setSeedPhrase(seedPhrase);
        setWallet(wallet);
        navigate("/yourwallet");
      } else {
        toast.error("Incorrect password!");
        setShowRecovery(true);
      }
    } else {
      toast.error("No wallet found!");
      navigate("/");
    }
  };

  return (
    <>
      <div className="content">
        <h2>Login to Wallet</h2>
        <Input.Password
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: "90%", marginBottom: "20px" }}
        />
        <Button
          type="primary"
          onClick={handleLogin}
          style={{ width: "90%", marginBottom: "20px" }}
        >
          Login
        </Button>
        
        {showRecovery && (
          <>
            <p style={{ margin: "10px 0" }}>Forgot your password?</p>
            <Button
              type="default"
              onClick={() => navigate("/recover")}
              style={{ width: "90%" }}
            >
              Recover with Seed Phrase
            </Button>
          </>
        )}
        
        <p className="frontPageBottom" onClick={() => navigate("/")}>
          Back Home
        </p>
      </div>
    </>
  );
}

export default PasswordLogin;
