import React, { useState } from "react";
import { Button, Input } from "antd";
import { useNavigate } from "react-router-dom";
import { ethers } from "ethers";
import { toast } from 'react-toastify';
import { encryptData } from '../utils/passwordManager';

function RecoverAccount({ setSeedPhrase, setWallet }) {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [recoveryPhrase, setRecoveryPhrase] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const validateAndProceed = () => {
    if (!recoveryPhrase.trim()) {
      toast.error("Please enter your seed phrase");
      return;
    }
    
    setIsLoading(true);
    try {
      // Validate the seed phrase
      ethers.Wallet.fromPhrase(recoveryPhrase);
      setStep(2);
    } catch (error) {
      toast.error("Invalid seed phrase! Please check and try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleRecover = () => {
    if (!password || !confirmPassword) {
      toast.error("Please enter and confirm your password");
      return;
    }
    if (password !== confirmPassword) {
      toast.error("Passwords don't match!");
      return;
    }
    if (password.length < 8) {
      toast.error("Password must be at least 8 characters!");
      return;
    }

    setIsLoading(true);
    try {
      const wallet = ethers.Wallet.fromPhrase(recoveryPhrase);
      const walletData = {
        seedPhrase: recoveryPhrase,
        wallet: wallet.address,
        hashedPassword: btoa(password)
      };

      const encryptedData = encryptData(walletData, password);
      localStorage.setItem('encryptedWalletData', encryptedData);

      setSeedPhrase(recoveryPhrase);
      setWallet(wallet.address);
      toast.success("Wallet recovered successfully!");
      navigate("/yourwallet");
    } catch (error) {
      toast.error("Error recovering wallet. Please try again.");
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      if (step === 1) {
        validateAndProceed();
      } else {
        handleRecover();
      }
    }
  };

  return (
    <div className="content">
      {step === 1 ? (
        <>
          <h2>Recover Wallet</h2>
          <p style={{ color: "white", marginBottom: "20px" }}>
            Enter your 12-word seed phrase
          </p>
          <Input.TextArea
            placeholder="Enter seed phrase..."
            value={recoveryPhrase}
            onChange={(e) => setRecoveryPhrase(e.target.value)}
            onKeyPress={handleKeyPress}
            style={{ width: "90%", marginBottom: "20px", height: "100px" }}
          />
          <Button
            type="primary"
            onClick={validateAndProceed}
            loading={isLoading}
            style={{ width: "90%" }}
          >
            Continue
          </Button>
        </>
      ) : (
        <>
          <h2>Set New Password</h2>
          <Input.Password
            placeholder="Create new password (min 8 characters)"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onKeyPress={handleKeyPress}
            style={{ width: "90%", marginBottom: "20px" }}
          />
          <Input.Password
            placeholder="Confirm new password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            onKeyPress={handleKeyPress}
            style={{ width: "90%", marginBottom: "20px" }}
          />
          <Button
            type="primary"
            onClick={handleRecover}
            loading={isLoading}
            style={{ width: "90%" }}
          >
            Recover Wallet
          </Button>
        </>
      )}
      <p className="frontPageBottom" onClick={() => navigate("/")}>
        Back Home
      </p>
    </div>
  );
}

export default RecoverAccount;
