import React, { useEffect, useState } from "react";
import { Button, Input } from "antd";
import { useNavigate } from "react-router-dom";
import { toast } from 'react-toastify';

function Home() {
  const navigate = useNavigate();
  const [hasWallet, setHasWallet] = useState(false);
  const [password, setPassword] = useState("");

  useEffect(() => {
    // Check if wallet data exists in local storage
    const encryptedWalletData = localStorage.getItem('encryptedWalletData');
    setHasWallet(!!encryptedWalletData);
  }, []);

  const handlePasswordLogin = () => {
    if (!password) {
      toast.error("Please enter your password");
      return;
    }
    navigate("/password-login");
  };

  return (
    <>
      <div className="content">
        <h2>Best Crypto Wallet</h2>
        <h4 className="h4">Secure, Self Custodial, Decentralized</h4>
        
        {hasWallet ? (
          // Show password login if wallet exists
          <>
            <p style={{ color: "white", marginBottom: "20px" }}>
              Enter your password to access your wallet
            </p>
            <Input.Password
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handlePasswordLogin()}
              style={{ width: "90%", marginBottom: "20px" }}
            />
            <Button
              onClick={handlePasswordLogin}
              className="frontPageButton"
              type="primary"
            >
              Unlock Wallet
            </Button>
            <p 
              className="frontPageBottom" 
              onClick={() => navigate("/recover")}
              style={{ cursor: "pointer" }}
            >
              Recover Account using Seed Phrase
            </p>
          </>
        ) : (
          // Show create/import options if no wallet exists
          <>
            <Button
              onClick={() => navigate("/create-wallet")}
              className="frontPageButton"
              type="primary"
            >
              Create A Wallet
            </Button>
            <Button
              onClick={() => navigate("/import-wallet")}
              className="frontPageButton"
              type="default"
            >
              Import Existing Wallet
            </Button>
            <p 
              className="frontPageBottom" 
              onClick={() => navigate("/recover")}
              style={{ cursor: "pointer" }}
            >
              Recover Account using Seed Phrase
            </p>
          </>
        )}
      </div>
    </>
  );
}

export default Home;
